import React, { Component } from 'react';
import {BrowserRouter as Router, Route} from 'react-router-dom';
import axios from 'axios';
import Header from './components/AppNavbar';
import Todos from './components/Emails';
import AddTodo from './components/AddEmail';
//import About from './components/pages/About';

//import  uuid from 'uuid';

import './App.css';



class App extends Component {
  state = { 
    todos: []
  }

  componentDidMount() {
    axios.get('https://jsonplaceholder.typicode.com/todos?_limit=10')
      .then(res => this.setState({todos: res.data}));
  }

  // Toggle the "completed" part in state when onChange is clicked 
  markComplete = (id) => {
    this.setState({todos: this.state.todos.map(todo =>{
      if (todo.id === id) {
        todo.completed = !todo.completed;
      }
      return todo;
    }) 
      })
  }
  // Delete Todo
  delTodo = (id) => { 
    axios.delete(`https://jsonplaceholder.typicode.com/todos/${id}`)
      .then(res => this.setState({todos: [...this.state.todos.filter(todo => todo.id !== id)]}));
  }
  //Add Todo: 
  //Making the request to JSONPLACEHOLDER and it sends a response back with to do information including the ID
  // and it got added to the UI
  addTodo = (title) => {
    axios.post('https://jsonplaceholder.typicode.com/todos', {
      title,
      completed:false
    })
      .then(res => this.setState({todos: [...this.state.todos, res.data]}))

    // const newTodo = {
    //   id: uuid.v4(),
    //   title, 
    //   completed: false
    // }
    //this.setState({todos: [...this.state.todos, newTodo]});
  }

  render() {
    return (
      <Router>
        <div className="App">
          <div className= "container">
            <Header />
            <Route exact path = "/" render = {props => (
              <React.Fragment>
                <AddEmail addTodo = {this.addTodo} />
                <Todos todos = {this.state.todos} 
                markComplete = {this.markComplete}
                delTodo = {this.delTodo}/>
                </React.Fragment>
            )} />
            {/* <Route path = "/about" component = {About} /> */}
          </div>
        </div>
      </Router>
    );
  }
}

export default App;



