import React, { Component } from 'react'; 
//import PropTypes from 'prop-types';

export class EmailItem extends Component {
  getStyle = () => {
    return {
      background: '#f4f4f4', 
      padding: '10px',
      borderBottom: '1px #ccc dotted'
    }
  }

  render() {
    const {id, email} = this.props.email;
    return (
      <div style={this.getStyle()}>
        <p>
          <input/>{email}
          <button onClick = {this.props.delEmail.bind(this,id)} style = {btnStyle}>x</button>
        </p>
      </div>
    )
  }
}

