import React, { Component } from 'react';
import axios from 'axios';
import { Container, ListGroup, ListGroupItem, Button } from 'reactstrap';
import { CSSTransition, TransitionGroup } from 'react-transition-group';
import uuid from 'uuid';

class EmailList extends Component {
    // static propTypes = {
    //   getItems: PropTypes.func.isRequired,
    //   item: PropTypes.object.isRequired,
    //   isAuthenticated: PropTypes.bool
    // };

    // componentDidMount() {
    //   this.props.getItems();
    // }

    // onDeleteClick = id => {
    //   this.props.deleteItem(id);
    // };
    state = {
        emails: []
    }

    componentDidMount() {
        axios.get('api/forms')
          .then(res => this.setState({emails: res.data}));
    }


    render() {
      const { emails } = this.state;
      return (
        <Container>
            <Button
                color="dark"
                style={{marginBottom:'2rem'}}
                onClick={()=> {
                    const email=prompt('Enter Your Email');
                    if (email) {
                        this.setState(state => ({
                            emails: [...state.emails, {id: _id, email: email}]
                        }));
                    }
                }}
            >Add Email</Button>
            <ListGroup>
                <TransitionGroup className="email-list">
                {emails.map(({id, email})=> (
                    <CSSTransition key={id} timeout={500} className="fade">
                        <ListGroupItem>
                        <Button
                            className="remove-btn"
                            color="danger"
                            size="sm"
                            onClick={()=> {
                                this.setState(state => ({
                                    emails: state.emails.filter(item=> item.id !==id)
                                }));
                            }}
                        >&time;</Button>   
                        {email}
                        </ListGroupItem>
                    </CSSTransition>
                ))}
                </TransitionGroup>
            </ListGroup>
        </Container>


      )
    }
  }

export default EmailList; 

