import React, { Component } from 'react'; 
import PropTypes from 'prop-types';

export class EmailItem extends Component {
  getStyle = () => {
    return {
      background: '#f4f4f4', 
      padding: '10px',
      borderBottom: '1px #ccc dotted'
        }
  }

  render() {
    const {id, title} = this.props.email;
    return (
      <div style={this.getStyle()}>
      </div>
    )
  }
}

// PropTypes
EmailItem.propTypes = {
  email: PropTypes.object.isRequired
}

const btnStyle = {
  background: '#ff0000',
  color: '#fff', 
  border: 'none', 
  padding: '5px 8px', 
  borderRadius: '50%',
  cursor: 'pointer', 
  float: 'right'
}

export default Emailtem



